(function(){
  module.exports = function(whisper){
    return whisper.task('help', [], "Displays usage information for a given task.\n\nUsage: whisper help [task]", function(env, name){
      var task, e;
      switch (false) {
      case !!name:
        return whisper.log.fatal("`help` expects a task name, none was given.");
      default:
        try {
          task = whisper.resolve(name);
          return console.log("# " + name + "\n\n" + task.description);
        } catch (e$) {
          e = e$;
          switch (e.name) {
          case '<inexistent-task-e>':
            return whisper.log.fatal("The task \"" + name + "\" is not registered.");
          }
        }
      }
    });
  };
}).call(this);
