(function(){
  module.exports = function(whisper){
    var fs, pkg, readJson;
    fs = require('fs');
    pkg = require('../../package.json');
    readJson = function(name){
      return JSON.parse(fs.readFileSync(name, 'utf-8'));
    };
    return whisper.task('version', [], "Displays Whisper's version.", function(env){
      return console.log("Whisper " + pkg.version);
    });
  };
}).call(this);
